<?phpclass View {    public function render($name, $header = 0, $footer = 0, $css = array(), $js = array()) {        /*         *   -1 : No Header         *    0 : Header         *    1 : Index Header         *    2 : Simple Header         *    3 : Admin Header         *    4 : Admin No Header         *    5 : Franchise header         *   -1 : No Footer         *    0 : Footer         *    1 : Index Footer         *    2 : Simple Footer         *    3 : Admin Footer         *    4 : Admin No Footer         */        switch ($header) {            case -1:                $header_page = 'no_header';                break;            case 0:                $header_page = 'header';                break;            case 1:                $header_page = 'index_header';                break;            case 2:                $header_page = 'simple_header';                break;            case 3:                $header_page = 'admin_header';                break;            case 4:                $header_page = 'admin_no_header';                break;            case 5:                $header_page = 'franchise_header';                break;        }        switch ($footer) {            case -1:                $footer_page = 'no_footer';                break;            case 0:                $footer_page = 'footer';                break;            case 1:                $footer_page = 'index_footer';                break;            case 2:                $footer_page = 'simple_footer';                break;            case 3:                $footer_page = 'admin_js_loader';                break;            case 4:                $footer_page = 'admin_no_footer';                break;        }        require_once 'views/' . $header_page . '.php';        require_once 'views/' . $name . '.php';        require_once 'views/' . $footer_page . '.php';    }}